config.matchQuery:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX text: <http://jena.apache.org/text#>
PREFIX pf: <http://jena.hpl.hp.com/ARQ/property#>
PREFIX sf: <http://ldf.fi/similarity-functions#>
PREFIX crm: <http://www.cidoc-crm.org/cidoc-crm/>
PREFIX sdfbs: <http://ldf.fi/sdfb/schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT ?queryId ?entity (SAMPLE(?l) AS ?label)  (GROUP_CONCAT(DISTINCT ?al;separator=", ") AS ?Alternate_names) (CONCAT("<span style='background-color:red'>",COALESCE(MAX(?bwarning),""),COALESCE(MAX(?dwarning),""),"</span>") AS ?Warnings) (GROUP_CONCAT(DISTINCT ?pl;separator=", ") AS ?Description) (CONCAT(COALESCE(STR(YEAR(MAX(?bd))),"?"),"-",COALESCE(STR(YEAR(MAX(?dd))),"?")) AS ?Lifespan) (MAX(?sc) AS ?score) (SAMPLE(?url) AS ?Link) {
  { # QUERY
    {
      SELECT ?e ?mlabel (SUM(?s)/COUNT(?s) AS ?sc) {
        {
          SELECT DISTINCT ?e {
            BIND(REPLACE(REPLACE(REPLACE(REPLACE(<QUERY>,"([\\+\\-\\&\\|\\!\\(\\)\\{\\}\\[\\]\\/\\^\\\"\\~\\*\\?\\:\\\\])","\\\\$1"),"^ +| +$", ""),", *"," "),"\\. ","* ") AS ?escapedQuery)
            BIND(CONCAT("(",?escapedQuery,") OR (",REPLACE(?escapedQuery,"([^*]) +","$1~ "),"~)") AS ?queryTerm)
            ?e text:query ?queryTerm .
            ?e a crm:E21_Person .
          }
          LIMIT 30
        }
        ?e rdfs:label|skos:prefLabel|skos:altLabel ?mlabel .
        ?str pf:strSplit (<QUERY> " ")
        BIND(sf:levenshteinSubstring(?str,STR(?mlabel)) AS ?s)
      } GROUP BY ?e ?mlabel
    }
    BIND(<QUERY_ID> AS ?queryId)
    FILTER(BOUND(?e))
    OPTIONAL {
      ?e sdfbs:dateOfDeath/crm:P82b_end_of_the_end ?dd .
      BIND(IF(STRDT(REPLACE(<CELL_1>,"-.*",""),xsd:integer)>YEAR(?dd),"Died before! ","") AS ?dwarning)
    }
    OPTIONAL {
      ?e sdfbs:dateOfBirth/crm:P82a_begin_of_the_begin ?bd .
      BIND(IF(YEAR(?bd)>STRDT(REPLACE(<CELL_1>,".*-",""),xsd:integer),"Wasn't born! ","") AS ?bwarning)
    }
  } # /QUERY
  ?e skos:prefLabel ?l .
  BIND(?e as ?entity)
  OPTIONAL {
    ?e dcterms:description ?pl .
  }
  OPTIONAL {
    ?e skos:altLabel ?al .
  }
  BIND(CONCAT('<a href="http://www.sixdegreesoffrancisbacon.com/people/',SUBSTR(STR(?entity),27),'" target="_blank">[o]</a>') AS ?url)
}
GROUP BY ?queryId ?entity
ORDER BY ?queryId DESC(?score)
